       WHERE NOT EXISTS ( SELECT *
                          FROM USER_PREFERENCES
                             WHERE TO_CHAR(USER_ID) = 'DO_NOT_DELETE_OR_UPDATE_AMM_VERSION' AND TO_CHAR(COLUMN_PREFERENCES) = '4.3');
