LEFT JOIN CSM_CODESET SRCCODESET
ON SRCCODES.CODESET_ID = SRCCODESET.CODESET_ID
LEFT JOIN CSM_CATEGORY SRCCATEGORY
ON SRCCATEGORY.CATEGORY_ID = SRCCODESET.CATEGORY_ID
LEFT JOIN CSM_CODE_VALUE TGTCODES
