LEFT JOIN CSM_CODESET TGTCODESET
ON TGTCODES.CODESET_ID = TGTCODESET.CODESET_ID
LEFT JOIN CSM_CATEGORY TGTCATEGORY
ON TGTCATEGORY.CATEGORY_ID = TGTCODESET.CATEGORY_ID)';
