        PRAGMA AUTONOMOUS_TRANSACTION;
        CYCLEIDS_COUNT    NUMBER;
        COUNTER  NUMBER :=1;
		    NXT_LIN_CYCID NUMBER;
        CURR_LIN_CYCID NUMBER;
