TEMP_OBJECT_ID INT;
TABLECHECKERFLAG1 INT;
TABLECHECKERFLAG2 INT;
TABLECHECKERFLAG3 INT;
TEMP_KV_ID INT;
TEMP_KV_TYPE_ID INT;
BEGIN
    SELECT  OBJECT_TYPE_ID INTO TEMP_OBJECT_TYPE_ID FROM ADS_KEY_VALUE_OBJECTS WHERE OBJECT_TYPE='SM_DATABASE_OPTIONS';
