	AS
	  SELECT CASE
		   WHEN TEST_DISPOSITION = ''U'' THEN ''UNASSIGNED''
		   ELSE CASE
			  WHEN TEST_DISPOSITION = ''P'' THEN ''PASSED''
			  ELSE CASE
				 WHEN TEST_DISPOSITION = ''F'' THEN ''FAILED''
				 ELSE CASE
					WHEN TEST_DISPOSITION = ''N'' THEN
					''NEEDS ANALYSIS''
					ELSE ''''
				      END
			       END
			END
