		 MAP_ID,
		 MAP_NAME,
		 MAP_SPEC_VERSION,
		 TESTING_NOTES,
		 PROJECT_ID,
		 PROJECT.PROJ_NAME  AS PROJECTNAME,
		 PROJECT.PROJ_DESCR AS PROJECTDESCRIPTION,
		 STATUS             AS MAPPINGSTATUS
	  FROM   MAPPING_DETAILS
		 LEFT JOIN PROJECT
		   ON PROJECT.PROJ_ID = MAPPING_DETAILS.PROJECT_ID');
END
