       LEFT( TGT_SYSTEM_NAME1, CHARINDEX(                 CHAR(10), TGT_SYSTEM_NAME1            +CHAR(10))-1),
       STUFF( TGT_SYSTEM_NAME1, 1, CHARINDEX(             CHAR(10), TGT_SYSTEM_NAME1            +CHAR(10)), ''''),
