       CAST(STUFF( TGT_COL_DATATYPE1, 1, CHARINDEX(  CHAR(10), TGT_COL_DATATYPE1                     +CHAR(10)), '''')AS VARCHAR(max)),
       CAST(LEFT( TGT_COL_LENGTH1, CHARINDEX(        CHAR(10), TGT_COL_LENGTH1                       +CHAR(10))-1) AS VARCHAR(max)),
