    ELSE
    BEGIN
        SELECT @countValue = COUNT(*) FROM  MAPPING_DETAILS B1 WHERE  B1.ISPUBLISHED = 'Y' AND B1.EFFECTIVE_DATE <= GETDATE() AND SUBJECT_ID in (select * from FN_SPLITTREENODEIDS(@SUBIDS,','))
