CREATE FUNCTION GREATEST(@v1 datetime, @v2 datetime=NULL, @v3 datetime=NULL)
RETURNS datetime AS
BEGIN
 DECLARE @return datetime
 SELECT TOP 1 @return=VALUE
 FROM (SELECT @v1 value UNION ALL SELECT @v2 UNION ALL SELECT @v3) a
 ORDER BY value DESC
 RETURN @return
END
