IF NOT EXISTS (SELECT *
	       FROM   SYSOBJECTS
	       WHERE  ID = OBJECT_ID(N'AMM_MASTER_CONFIGURATION')
		      AND TYPE = 'U')
  BEGIN
      EXECUTE ('CREATE TABLE AMM_MASTER_CONFIGURATION (
