		set @CURRENT_OBJ_SEQ_ID = 1;
	 END
		SET @SQLSTATEMENT = 'CREATE TABLE PROJECT2(
		PROJ_ID int NOT NULL IDENTITY('+CONVERT(VARCHAR(4000),@CURRENT_OBJ_SEQ_ID)+',1),
