		EXECUTE('ALTER TABLE PROJECT_RESOURCES ADD CONSTRAINT R_7 FOREIGN KEY (PROJ_ID) REFERENCES PROJECT(PROJ_ID) ON DELETE CASCADE');
	END TRY
	BEGIN CATCH
		ROLLBACK TRANSACTION PROJECTS_MOVE;
